-- INSERT  USERS  200 คน
DECLARE @i INT = 1;
WHILE @i <= 200
BEGIN
    VALUES (
        CONCAT('user', RIGHT('000' + CAST(@i AS NVARCHAR(3)), 3)),         -- Username เช่น user001
        '123456',                                                          -- Password เริ่มต้น
        CASE WHEN @i <= 5 THEN 'Admin' ELSE 'User' END,                    -- 5 คนแรกเป็น Admin
        CONCAT('First', @i),                                               -- FirstName
        CONCAT('Last', @i),                                                -- LastName
        CONCAT('Address No. ', @i, ' Bangkok'),                            -- Address
        CONCAT('081', RIGHT('000000' + CAST(@i AS NVARCHAR(6)), 6))        -- Phone เช่น 081000001
    );
    SET @i = @i + 1;
END;

